import React, { Component } from 'react';
import { connect } from 'react-redux';
import Input from '../components/Input';
import Button from '../components/Button';
import Select from '../components/Select';
import { actionChangeForm } from '../redux/actions/action';

class PersonalForm extends Component {
  constructor() {
    super();

    this.state = {
      nome: '',
      email: '',
      cpf: '',
      endereco: '',
      cidade: '',
      estado: '',
    };

    this.handleChange = this.handleChange.bind(this);
    this.handlePath = this.handlePath.bind(this);
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  handlePath() {
    const { handleUser, history } = this.props;
    handleUser(this.state);
    history.push('/professionalform');
  }

  render() {
    const { nome, email, cpf, endereco, cidade, estado } = this.state;
    const states = [
      'Rio de Janeiro',
      'Minas Gerais',
      'Amapá', 'Amazonas', 'São Paulo', 'Ceará', 'Distrito Federal'];
    return (
      <div>
        <fieldset className="div-personalForm">
          <Input
            label="nome: "
            type="text"
            onChange={ this.handleChange }
            value={ nome }
            name="nome"
            required
          />
          <Input
            label="email: "
            type="text"
            onChange={ this.handleChange }
            value={ email }
            name="email"
            required
          />
          <Input
            label="cpf: "
            type="text"
            onChange={ this.handleChange }
            value={ cpf }
            name="cpf"
            required
          />
          <Input
            label="endereco: "
            type="text"
            onChange={ this.handleChange }
            value={ endereco }
            name="endereco"
            required
          />
          <Input
            label="cidade: "
            type="text"
            onChange={ this.handleChange }
            name="cidade"
            value={ cidade }
          />
          <Select
            defaultOption="Selecione"
            onChange={ this.handleChange }
            value={ estado }
            label="Estado: "
            id="estado"
            name="estado"
            options={ states }
          />
          <Button
            type="button"
            label="Enviar"
            onClick={ this.handlePath }
          />
        </fieldset>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  handleUser: (state) => dispatch(actionChangeForm(state)),
});

export default connect(null, mapDispatchToProps)(PersonalForm);
